import React from "react";

class Trips extends React.Component {
    state = {
        Trips: []
    }
    componentDidMount() {
        fetch('https://trip-build-api.herokuapp.com/api/trips')
        .then(res => res.json())
        .then((data) => {
            this.setState({ trips: data })
            console.log(this.state.trips)
        })
        .catch(console.log)
    }
    render() {
        return (
            <div>
                <div className="row mb-3">
                    <div className="col-md-12 text-md-center">
                        <h2 className="text-poppins font-weight-bold">Trips</h2>
                    </div>
                </div>
                <div className="row justify-content-center">
                    {this.state.trips.map((trip) => (
                        <div className="col-md-8">
                        <div className="card card-outline translate-up mb-3">
                            <div className="card-body">
                                <h5 className="card-title"><strong className="text-primary">{trip.userId}</strong> | {trip.title}</h5>
                                <h6 className="body-1 text-muted">{trip.body}</h6>
                            </div>
                            <div className="card-footer text-right">
                                <h6 className="subtitle-1 text-muted">Hace 1 min.</h6>
                            </div>
                        </div>
                    </div>
                    ))}
                </div>
            </div>
        );
    }
}

export default Trips;
